if (flow.get("mode") == undefined) {
    flow.set("mode", "0");
}
if (flow.get("curMode") == undefined) {
    flow.set("curMode", "comfort");
}
if (flow.get("comfortFlow") == undefined) {
    flow.set("comfortFlow", 100);
}
if (flow.get("targetFlow") == undefined) {
    flow.set("targetFlow", 100);
}
if (flow.get("absentFlow") == undefined) {
    flow.set("absentFlow", 50);
}
if (flow.get("presence") == undefined) {
    flow.set("presence", "false");
}
if (flow.get("presenceReaction") == undefined) {
    flow.set("presenceReaction", "nothing");
}
return msg;

var descr = { payload: "" };
var curMode = { payload: "" };


if (flow.get("mode") == "2") {
    msg.payload = 0;
    descr.payload = "Выключено";
    curMode.payload = "off";
} else if (flow.get("presence") == true && flow.get("presenceReaction") == "auto") {
    msg = null;
    descr.payload = "Авто 🏠";
    curMode.payload = "presence.auto";
} else if (flow.get("presence") == true && flow.get("presenceReaction") == "comfort") {
    msg.payload = flow.get("comfortFlow");
    descr.payload = "Комфорт 🏠";
    curMode.payload = "presence.comfort";
} else if (flow.get("mode") == "0") {
    msg.payload = flow.get("comfortFlow");
    descr.payload = "Комфорт";
    curMode.payload = "comfort";
} else if (flow.get("mode") == "1") {
    msg.payload = flow.get("absentFlow");
    descr.payload = "Отсутствие";
    curMode.payload = "absent";
} else if (flow.get("mode") == "3") {
    if (msg.hasOwnProperty("getStatus")) {
        if (context.get("lastVal") != undefined) {
            msg.payload = context.get("lastVal");
        } else {
            msg.payload = false;
        }
    }
    if (context.get("lastVal") == undefined) {
        context.set("lastVal", msg.payload);
        context.set("timer"), Date.now();
    }
    if (context.get("lastVal") != msg.payload) {
        context.set("lastVal", msg.payload);
        if (msg.payload == true) {
            context.set("timer", Date.now() + 20*60*1000);
        }
    }
    if (msg.payload == true && context.get("timer") > Date.now()) {
        msg.payload = 400; //going full speed for 20 minutes
        descr.payload = "Залповое проветривание 🕑";
        curMode.payload = "timer.inrush";
    } else {
        if (msg.payload == true) {
            msg.payload = flow.get("comfortFlow");
            descr.payload = "Комфорт 🕑";
            curMode.payload = "timer.comfort";
        } else {
            msg.payload = flow.get("absentFlow");
            descr.payload = "Отсутствие 🕑";
            curMode.payload = "timer.absent";
        }
    }
    return [msg, descr, curMode];
} else if (flow.get("mode") == "4") {
    msg = null;
    descr.payload = "Авто";
    curMode.payload = "auto";
}
return [msg, descr, curMode];

var iaq = {payload: 0};
if (msg.payload <= 65) {
    msg.payload = msg.payload + " ppb (Отлично)";
    iaq.payload = 1;
} else if (msg.payload <= 220) {
    msg.payload = msg.payload + " ppb (Хорошо)";
    iaq.payload = 2;
} else if (msg.payload <= 660) {
    msg.payload = msg.payload + " ppb (Приемлемо)";
    iaq.payload = 3;
} else if (msg.payload <= 2200) {
    msg.payload = msg.payload + " ppb (Неудовлетворительно!)";
    iaq.payload = 4;
} else {
    msg.payload = msg.payload + " ppb (Опасно для здоровья!)";
    iaq.payload = 5;
}
return [msg, iaq];

